// dashboard.component.ts
import { Component } from '@angular/core';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css'],
})
export class DashboardComponent {
  booksTakenCount: number = 0; // Implement logic to get the count
  booksReturnedCount: number = 0; // Implement logic to get the count
  availableBooksCount: number = 0; // Implement logic to get the count
}
